import React, { useState, useEffect } from 'react';
import { w3cwebsocket as W3CWebSocket } from "websocket";
import ReactFlow, { addEdge, MiniMap, Controls } from 'react-flow-renderer';
import { dataToConnections, connectionsToNodes } from './helper';

// sample connections array: {'NoiseGenerator': ['RollingAverager'], 'RollingAverager': ['AveragedNoise'], 'AveragedNoise': ['Plot']}

const InteractionGraph = () => {
  const [node_name, setNode] = useState("None");
  const [connections, setConnections] = useState([]);
  const [elements, setElements] = useState([]);

  // getting the node that the user is clicking 
  const onElementClick = (event, element) => {
    setNode(element.id)
    console.log("node", node_name)
  };

  // receiving the messages from server 
  useEffect(() => {
    // connecting to the server 
    const client = new W3CWebSocket('ws://localhost:9000');

    client.onopen = () => {
      console.log('connected');
      client.send(JSON.stringify({
        "api_version": "0.1",
        "api_request": {
          "request_id": 1,
          "start_stream_request": {
            "stream_id": "LABGRAPH.MONITOR",
             "labgraph.monitor": {
             }
          }
        }
     }))
    };

    client.onmessage = (message) => {
      const dataFromServer = JSON.parse(message.data);
      if (dataFromServer.stream_batch){
        const data_array = dataFromServer.stream_batch["labgraph.monitor"].samples[0].data;
        const connections = dataToConnections(data_array);
        const elements = connectionsToNodes(connections);
        setElements(elements)
        setConnections(connections);
        console.log("connection", connections)
        console.log("server message received ", data_array)
      }
    };

    return () => {
      client.close();
    }
  }, []);

  return(
  <div>
    <div>
      <table>
        <thead>
          <tr>
            <th>Node</th>
            <th>Message</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{node_name}</td>
            <td>Message 1</td>
          </tr>
        </tbody>
      </table>
    </div>

    <div style={{ height: 800 }}> 
    <ReactFlow 
    elements={elements}
    onElementClick={onElementClick}/>
    </div>

  </div>

  
  )
};

export default InteractionGraph;
